import java.util.Scanner;class sample{ public static boolean ispalin(int n) { int r,t=n,a=0; while(n>0) { r=n%10; a=a*10+r; n=n/10; } if(t==a) return true; else return false; } public static void main(String args[]) { Scanner sc=new Scanner(System.in); int n,i,a1,a2; n=sc.nextInt(); for(i=n+1;;i++) { if(ispalin(i)) { a1=i; break; } } for(i=n-1;;i--) { if(ispalin(i)) { a2=i; break; } } if((a1-n)==(n-a2)) System.out.println(a2+" "+a1); else if((a1-n)<(n-a2)) System.out.println(a1); else System.out.println(a2); }}